mapper.select:
select d.*,dt.*,pt.* from doctors d left join departments dt on d.department_id =dt.department_id left join professional_titles pt on d.professional_title_id=pt.id
d.department_id =#{departmentId}
and d.professional_title_id =#{titleId}
and d.name like concat('%',#{doctorName},'%')
and d.job_number like concat('%',#{jobNumber},'%')
order by d.doctor_id

mapper.update:
update doctors
set name = #{name}, phone = #{phone}, email = #{email}
where doctor_id = #{doctorId}

update doctors
set password = #{newPassword}
where doctor_id = #{doctorId}
