mapper.update:
update appointments 
set status = #{status}
where appointment_id = #{appointmentId}

mapper.select:
select count(*)
from appointments
where doctor_id = #{doctorId}
  and date(appointment_date) = date(#{appointmentDate})
  and appointment_time = #{appointmentTime}
  and status != 'cancelled'

select a.*, p.pname, p.phone as patient_phone, p.id_card_number, d.name, d.job_number, d.phone, dept.department_name from appointments a left join patients p on a.patient_id = p.patient_id left join doctors d on a.doctor_id = d.doctor_id left join departments dept on d.department_id = dept.department_id where a.doctor_id = #{doctorId}
and a.status = #{status}
order by a.appointment_date desc, a.createtime desc
